using Mediator.Switch.SourceGenerator.Exceptions;using Microsoft.CodeAnalysis;namespace Mediator.Switch.SourceGenerator.Generator;public static class BehaviorApplicabilityChecker{    public static bool IsApplicable(Compilation compilation,        ITypeSymbol behaviorClass,        IReadOnlyList<ITypeParameterSymbol> behaviorTypeParameters,        ITypeSymbol requestType,        ITypeSymbol responseType)    {        if (behaviorTypeParameters.Count != 2)        {            throw new SourceGenerationException("Closed behaviors are not supported in SwitchMediator. Use constraints on generic type parameters instead.", behaviorClass.Locations[0]);        }        return ConstraintChecker.IsConstraintSatisfied(compilation, behaviorTypeParameters[0], requestType) &&               ConstraintChecker.IsConstraintSatisfied(compilation, behaviorTypeParameters[1], responseType);    }}